import React from "react";
import * as Components from './Components';
import { Icon } from 'react-icons-kit';
import { eyeOff } from 'react-icons-kit/feather/eyeOff';
import { eye } from 'react-icons-kit/feather/eye';

function App() {
    const [signIn, toggle] = React.useState(true);
    const [showPassword, setShowPassword] = React.useState(false);

    const handleTogglePassword = () => {
        setShowPassword(!showPassword);
    };

    return(
        <Components.Container style={{ padding: '20px' }}>
            <Components.SignUpContainer signinIn={signIn}>
                <Components.Form style={{ width: '100%', maxWidth: '400px', margin: 'auto' }}>
                    <Components.Title>Registration form Doctor</Components.Title>
                    <Components.Input type='text' placeholder='Name' style={{ width: '100%' }} />
                    <Components.Input type='email' placeholder='Email' style={{ width: '100%' }} />
                    <Components.Input type='tel' placeholder='Phone Number' style={{ width: '100%' }} />
                    <Components.Input type='text' placeholder='Location' style={{ width: '100%' }} />
                    <Components.Input type='text' placeholder='Years of Service' style={{ width: '100%' }} />
                    <div style={{ position: 'relative', width: '100%' }}>
                        <Components.Input
                            type={showPassword ? 'text' : 'password'}
                            placeholder='Password'
                            style={{ width: '100%' }}
                        />
                        <Icon
                            icon={showPassword ? eyeOff : eye}
                            onClick={handleTogglePassword}
                            style={{
                                position: 'absolute',
                                top: '50%',
                                right: '10px',
                                transform: 'translateY(-50%)',
                                cursor: 'pointer'
                            }}
                        />
                    </div>
                    <div style={{ position: 'relative', width: '100%' }}>
                        <Components.Input
                            type={showPassword ? 'text' : 'password'}
                            placeholder='Confirm Password'
                            style={{ width: '100%' }}
                        />
                        <Icon
                            icon={showPassword ? eyeOff : eye}
                            onClick={handleTogglePassword}
                            style={{
                                position: 'absolute',
                                top: '50%',
                                right: '10px',
                                transform: 'translateY(-50%)',
                                cursor: 'pointer'
                            }}
                        />
                    </div>
                    <Components.Button style={{ width: '100%' }}>Sign Up</Components.Button>
                </Components.Form>
            </Components.SignUpContainer>

            <Components.SignInContainer signinIn={signIn}>
                <Components.Form style={{ width: '100%', maxWidth: '400px', margin: 'auto' }}>
                    <Components.Title>Log in</Components.Title>
                    <Components.Input type='email' placeholder='Email' style={{ width: '100%' }} />
                    <div style={{ position: 'relative', width: '100%' }}>
                        <Components.Input
                            type={showPassword ? 'text' : 'password'}
                            placeholder='Password'
                            style={{ width: '100%' }}
                        />
                        <Icon
                            icon={showPassword ? eyeOff : eye}
                            onClick={handleTogglePassword}
                            style={{
                                position: 'absolute',
                                top: '50%',
                                right: '10px',
                                transform: 'translateY(-50%)',
                                cursor: 'pointer'
                            }}
                        />
                    </div>
                    <Components.Anchor href='#' style={{ display: 'block', marginTop: '10px', textAlign: 'right' }}>Forgot your password?</Components.Anchor>
                    <Components.Button style={{ width: '100%' }}>Submit</Components.Button>
                </Components.Form>
            </Components.SignInContainer>

            <Components.OverlayContainer signinIn={signIn}>
                {/* OverlayContainer Content */}
            </Components.OverlayContainer>
        </Components.Container>
    )
}

export default App;
