import React, { useState } from 'react';
import styled from 'styled-components';
import PasswordToggle from './PasswordToggle'; // Import the PasswordToggle component

const PasswordInputWrapper = styled.div`
  position: relative;
`;

const Input = styled.input`
  background-color: #eee;
  border: none;
  padding: 12px 15px;
  margin: 8px 0;
  width: 100%;
`;

const Form = styled.form`
  background-color: #ffffff;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  padding: 0 50px;
  height: 100%;
  text-align: center;
`;

const Title = styled.h1`
  font-weight: bold;
  margin: 0;
`;

const PasswordInput = ({ ...props }) => {
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <PasswordInputWrapper>
      <Input type={showPassword ? 'text' : 'password'} {...props} />
      <PasswordToggle visible={showPassword} toggleVisibility={togglePasswordVisibility} />
    </PasswordInputWrapper>
  );
};

export { Form, Title, PasswordInput }; // Export PasswordInput along with Form and Title
